(function() {
    'use strict';

    angular
        .module('mainApp').controller("EbsController", EbsController);


    EbsController.$inject = ['$rootScope', '$scope', '$http'];
    function EbsController ($rootScope, $scope, $http) {
        var vm = this;

        $rootScope.$on('ntp-price', function(d,data) {
                   //子级得不到值
            vm.p = data;
            $("#alert-price").fadeTo(2000, 500).slideUp(300, function(){
                $("#alert-price").hide();
            });
        });
        $http.get('/test.json').success(function (data) {
            alert(data);
            vm.orders = data;
        });
    }
})();
