(function() {
    'use strict';

    angular
        .module('mainApp').controller("NtpController", NtpController);


    NtpController.$inject = ['$rootScope', '$scope','$http'];
    function NtpController ($rootScope, $scope, $http) {
        var vm = this;

        vm.price = function(){
            $rootScope.$broadcast('ntp-price', vm.p);
        }
    }
})();
