(function() {
    'use strict';

    //定义controller
    angular
        .module('mainApp').controller("SupportController", SupportController);



    //注入依赖服务
    SupportController.$inject = ['$rootScope', '$http'];



    //实现controller
    function SupportController ($rootScope, $http) {
        var vm = this;

        $rootScope.$on('ntp-price', function(d, data) {
                   //子级得不到值
            vm.p = data;
            $("#alert-price").fadeTo(2000, 500).slideUp(300, function(){
                $("#alert-price").hide();
            });


            $http.get('/test/test.json').success(function (data) {
                vm.orders = data;
            });
        });
    }

})();
